<section class="lg:container mt-40">
	<div class="flex justify-center">
    <h1 class="font-fef tracking-fef text-gray-500">OUR CLIENTS</h1>
  </div>
  <div class="mt-10 grid grid-cols-2 lg:grid-cols-clients place-items-center gap-3">
		{#each clientLogos as client }
		<div class="lg:max-w-8 max-w-6-1/2">
			{#if client.svg !== ''}
			{@html client.svg}
			{:else}
				<img src="clients/{client.logo}" alt="{client.name}" />
			{/if}
		</div>
    {/each}
  </div>
</section>
